function App() {
  const [showForm, setshowForm] = useState(false);
  const openForm = () => {
    setshowForm
  }
  return (
    <>
      <Header />
      {showForm ? <NewFactForm /> : null}

      <main className='main'>
        <CategoryFilters />
        <FactList />

      </main>
    </>
  )
}

export default App
