import React from 'react'
import './style.css';

const CATEGORIES = [
    { name: "technology", color: "#3b82f6" },
    { name: "science", color: "#16a34a" },
    { name: "finance", color: "#ef4444" },
    { name: "society", color: "#eab308" },
    { name: "entertainment", color: "#db2777" },
    { name: "health", color: "#14b8a6" },
    { name: "history", color: "#f97316" },
    { name: "news", color: "#8b5cf6" },
];


function CategoryFilters({ setcurrentCategory }) {
    return (
        <><aside>
            <ul>
                <li><button onClick={() => setcurrentCategory("all")} className="btn btn-all-categories">All</button></li>
                {CATEGORIES.map((cat) =>
                    <li key={cat.name} className="category">
                        <button className="btn btn-category" onClick={() => setcurrentCategory(cat.name)} style={{ backgroundColor: cat.color }}>{cat.name}</button>
                    </li>
                )}
            </ul>
        </aside></>
    );
}

export default CategoryFilters
